/*
	Piratey style guide:

	your = yer
	you_ = ye
	to_ = t'
	the_ = th'
	of_ = o'
	ing_ = in' (be careful - no kindling, sling, swing, ring, string, bring, sapling, being) - will also need to search ing_, ing., ing", etc.
	treasure = booty (but no treasure chest/treasure map)
	_are_ = be
	_is_ = be
	there = thar
	where = whar
	for = fer
	find = come across (not always, sometimes doesn't make sense)
	any = some godforsaken (doesn't work all the time)
	inventory = duffle
	riches = swag
	never = ne'er
	around = aroun'
	left = port (not always)
	right = starboard (not always)
	egg/eggs = cackle fruit
	chicken = cackle
	ghost = dredgie
	picked up = looted
	pick up = loot
	pick-up = loot
	good men = hearties
	Ye Died = Ye Be Shark Bait 
	gold coins = dubloon
	quickly = smartly
	quicker = smartly
	quick = smartly
	stopped = belayed
	stop = belay
	depths = briny depths
	quit = abort
	sandstone = holystone

	not used, other ideas:
	! = use blimey, avast (for stopping), or arrr (for yes)
	? = Savvy?

	Use textdiff: https://www.diffchecker.com/

	var rows = document.getElementsByClassName('diff-row');
	for (var i = 0; i < rows.length; i++) {
		rows[i].children[0].remove();
		rows[i].children[0].remove();
		rows[i].children[0].remove();
	}

	var equalRows = document.getElementsByClassName('diff-line-equal');
	while (equalRows[0]) {
		equalRows[0].parentNode.removeChild(equalRows[0]);
	}
*/

