/*
	Piratey style guide:

	Always match case first

	yourself = yerself
	Your_ _your = Yer yer
	You_ _you_ = Ye ye
	To_ _to_ = T' t'
	The_ _the_ = Th' th'
	_of_ = o'
	ing_ ing. ing" ing! = in' (be careful - no kindling, sling, swing, ring, string, bring, sapling, being, spring, flooring, bogling, warning)
	treasure = booty (but no treasure chest/treasure map)
	_are_ are_ are. = be
	_is_ = be
	there_ _there = thar
	where = whar
	_for_ for. = fer
	find = come across (not always, sometimes doesn't make sense)
	any_ _any = some godforsaken (doesn't work all the time)
	inventory = duffle
	riches = swag
	never = ne'er
	around = aroun'
	left = port (not always)
	right = starboard (not always)
	egg/eggs = cackle fruit
	chicken = cackle
	ghost = dredgie
	picked up = looted
	pick up = loot
	pick-up = loot
	good men = hearties
	Ye Died = Ye Be Shark Bait 
	gold coins = dubloons
	quickly = smartly
	quicker = smartly
	quick = smartly
	stopped = belayed
	stops = belays
	stop = belay
	depths = briny depths
	quit = abort
	sandstone = holystone

	not used, other ideas:
	! = use blimey, avast (for stopping), or arrr (for yes)
	? = Savvy?

	Use textdiff: https://www.diffchecker.com/

	var rows = document.getElementsByClassName('diff-row');
	for (var i = 0; i < rows.length; i++) {
		rows[i].children[0].remove();
		rows[i].children[0].remove();
		rows[i].children[0].remove();
	}

	var equalRows = document.getElementsByClassName('diff-row');
	for (var i = 0; i < equalRows.length; i++) {
		if (!equalRows[i].children[0].classList.contains('diff-line-modified')) {
			equalRows[i].remove();
			i--;
		}
	}
	
*/

